mapper.select:
/* 권한 리스트 getList org/ezdevgroup/common/persist/acl/auth/AuthMapper.xml */ SELECT AUTH_CD AS authCd , AUTH_NM AS authNm , AUTH_DC AS authDc , DATE_FORMAT(STR_TO_DATE(REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt , DATE_FORMAT(STR_TO_DATE(MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt FROM ACL_AUTH
AND ${searchKey} like '%${searchKwd}%'
ORDER BY AUTH_NM

mapper.delete:
/*
	권한에 대한 리소스 삭제
	removeAuthResource
	org/ezdevgroup/common/persist/acl/auth/AuthMapper.xml
*/
DELETE
FROM ACL_AUTH_RESOURCE
WHERE AUTH_CD = #{authCd}

	AND RESOURCE_CD = #{resourceCd}
