mapper.select:
/* User 정보 Object Or List getList org/ezdevgroup/common/persist/acl/user/user-mapper.xml */ SELECT U.USER_ID AS userId , U.USER_TYPE AS userType , CONCAT(U.USER_ID, U.USER_TYPE) AS userAuthKey , U.USER_NM AS userNm , UA.AUTH_CD AS authCd , A.AUTH_NM AS authNm FROM EZ_USER U LEFT OUTER JOIN ACL_USER_AUTH UA ON (CONCAT(U.USER_ID, U.USER_TYPE) = UA.USER_AUTH_KEY) LEFT OUTER JOIN ACL_AUTH A USING (AUTH_CD)
AND ${searchKey} like '%${searchKwd}%'
AND U.USER_ID = ${userId}
ORDER BY U.USER_NM

/* user List Cnt getListCnt org/ezdevgroup/common/persist/acl/user/user-mapper.xml */ SELECT COUNT(*) FROM EZ_USER U LEFT OUTER JOIN ACL_USER_AUTH UA ON (CONCAT(U.USER_ID, U.USER_TYPE) = UA.USER_AUTH_KEY) LEFT OUTER JOIN ACL_AUTH A USING (AUTH_CD)
AND ${searchKey} like '%${searchKwd}%'
AND U.USER_ID = ${userId}
ORDER BY U.USER_NM
